ruleToc.chapterList:
@js:
var doc = org.jsoup.Jsoup.parse(result);
var list = doc.select('#episodes a, #extra_episodes a').toArray().map(a=>({href:a.toString().match(/\/(\d+)\.html/)[1],text:a.text()}));

function swap(A, i, j) {
  let temp = A[i];
  A[i] = A[j];
  A[j] = temp;
}
function partition(A, l, r) {
  let x = A[r-1].href;
  let i = l-1;
  for (let j = l; j < r-1; j++) {
    if (A[j].href <= x) {
      i++;
      swap(A, i, j);
    }
  }
  swap(A, i+1, r-1);
  return i+1;
}
function quicksort(A, l, r) {
  l = l || 0;
  r = r || A.length;
  if (l < r-1) {
    let p = partition(A, l ,r);
    quicksort(A, l, p);
    quicksort(A, p+1, r);
  }
  return A;
}
quicksort(list, 0, list.length)

