ruleToc.chapterList:
@js:
eval(result.match(/var comic = \{.*?\}/)[0]);
function CustomSubString(str, nStart, nLength, bHideEnglish) {
	var s = str.substring(nStart, nStart + nLength);
	return bHideEnglish == null ? s.replace(/[a-z]*/gi, "") : s
}
var nComicId = comic.id;
var strFullCode = comic.code;
var mCodes = new Array();
var nSpecialCount = 0;
for (var i = 0; i < strFullCode.length / 50; i++) {
  mCodes.push(strFullCode.substring(i * 50, (i + 1) * 50));
}
var retMatrix = new Array();
for (var i = 0; i < mCodes.length; i++) {
  var strCode = mCodes[i];
  var nEpisodeId = parseInt(CustomSubString(strCode, 0, 4));
  var nServerId = parseInt(CustomSubString(strCode, 4, 2));
  var nRnd = parseInt(CustomSubString(strCode, 6, 1));
  var nPageCount = parseInt(CustomSubString(strCode, 7, 3));
  if (nEpisodeId >= 8000) {
    nSpecialCount++;
    retMatrix[i] = '{"id":"' + i + '","title":"番外篇' + nSpecialCount +'"}';
  } else {
    var unit = (nPageCount > 50) ? "卷": "話";
    retMatrix[i] = '{"id":"' + i + '","title":"第' + nEpisodeId + unit + '"}';
  }
}
retMatrix

