@put:{cid:$.data.detail.comic_id}<js>
var list = [];
var request_body;
var json;
var jsonp;
request_body = "comic_id=@get:{cid}&uid=0&page=1&remember_token=0&platfrom=1&order=1";
json = JSON.parse(java.fetch("https://pay.myfcomic.com/api/comic/getchapterlist",{
    method:"post",
    body: request_body
}).body().string());
jsonp = json.data.all_page;
list = list.concat(json.data.list);
for(var i=1; i<jsonp; i++){
request_body = "comic_id=@get:{cid}&uid=0&page=" + (i+1) + "&remember_token=0&platfrom=1&order=1"
json = JSON.parse(java.fetch("https://pay.myfcomic.com/api/comic/getchapterlist",{
    method:"post",
    body: request_body
}).body().string());
list = list.concat(json.data.list);
}
list
</js>

